{_AzurePIMEvents_Parser}
let TimeStart = todatetime({TimeRange:start});
let TimeEnd = todatetime({TimeRange:end});
let TimeGrain = totimespan({TimeRange:grain});
_AzurePIMEvents
| make-series Timeline = count() default = 0 on TimeGenerated from TimeStart to TimeEnd step TimeGrain by OperationName
| extend Count = array_sum(Timeline)
| project OperationName, Count, Timeline
| order by Count desc

{_AzurePIMEvents_Parser}
let TimeStart = todatetime({TimeRange:start});
let TimeEnd = todatetime({TimeRange:end});
let TimeGrain = totimespan({TimeRange:grain});
_AzurePIMEvents
| make-series Timeline = count() default = 0 on TimeGenerated from TimeStart to TimeEnd step TimeGrain by Object, ObjectType
| extend Count = array_sum(Timeline)
| project Object, ObjectType, Count, Timeline
| order by Count desc

{_AzurePIMEvents_Parser}
let TimeStart = todatetime({TimeRange:start});
let TimeEnd = todatetime({TimeRange:end});
let TimeGrain = totimespan({TimeRange:grain});
_AzurePIMEvents
| where InitiatedByObject !in ("MS-PIM", "Azure AD PIM")
| make-series Timeline = count() default = 0 on TimeGenerated from TimeStart to TimeEnd step TimeGrain by InitiatedByObject, InitiatedByType
| extend Count = array_sum(Timeline)
| project InitiatedByObject, InitiatedByType, Count, Timeline
| order by Count desc

