let ServiceNameValue = "Sentinel";
let InputCustomPrices = {CustomPrices};
let FixedOrPercentage = "{FixedOrPercentage}";
let Inputs = datatable(TierOrder:int, Tier: string, CustomPriceValue:real, PercentageDiscountValue:int)[
    0, "Pay-as-you-go", {SentinelPayasyougoFixed},      {SentinelPayasyougoPercent},
    1, "100",           {SentinelOneHundredFixed},      {SentinelOneHundredPercent},  
    2, "200",           {SentinelTwoHundredFixed},      {SentinelTwoHundredPercent},
    3, "300",           {SentinelThreeHundredFixed},    {SentinelThreeHundredPercent},
    4, "400",           {SentinelFourHundredFixed},     {SentinelFourHundredPercent},
    5, "500",           {SentinelFiveHundredFixed},     {SentinelFiveHundredPercent},
    6, "1000",          {SentinelOneThousandFixed},     {SentinelOneThousandPercent},
    7, "2000",          {SentinelTwoThousandFixed},     {SentinelTwoThousandPercent},
    8, "5000",          {SentinelFiveThousandFixed},    {SentinelFiveThousandPercent}
];
let Prices = materialize(
    {SentinelExternalData}
    | summarize arg_max(TimeGenerated, *) by Tier
    | project Tier, RetailPrice
    | join kind = inner (
        Inputs
    ) on Tier
    | project-away Tier1
    | extend SkuPrice = case(
        InputCustomPrices == true and FixedOrPercentage == "Fixed", CustomPriceValue,
        InputCustomPrices == true and FixedOrPercentage == "Percentage", RetailPrice - ((RetailPrice / 100) * PercentageDiscountValue),
        RetailPrice
    )
    | extend SkuEffectivePricePerGB = iff(
                Tier == "Pay-as-you-go", SkuPrice, 
                SkuPrice / toint(Tier) 
    )
    | project TierOrder, Tier, RetailPrice, SkuPrice, SkuEffectivePricePerGB
);
let PricesBag = toscalar(
    Prices
    | extend Bag = bag_pack(tostring(TierOrder), bag_pack("SkuEffectivePricePerGB", SkuEffectivePricePerGB, "Tier", Tier ))
| summarize make_bag(Bag));
Prices 
| extend PreviousTier = PricesBag.[tostring(TierOrder-1)].Tier
| extend PreviousTierSkuEffectivePricePerGB = toreal(PricesBag[tostring(TierOrder-1)].SkuEffectivePricePerGB)
| extend SkuEffectiveCommitmentTierThreshold = iff(
    Tier == "Pay-as-you-go", toreal(0),
    SkuPrice / PreviousTierSkuEffectivePricePerGB
)
| project Packed = pack_array(
    Tier, 
    SkuRetailPrice = RetailPrice,
    SkuPrice = round(SkuPrice, 4),
    SkuEffectivePricePerGB = round(SkuEffectivePricePerGB, 4),
    SkuEffectiveCommitmentTierThreshold = round(SkuEffectiveCommitmentTierThreshold, 4)
)
| summarize Packed = tostring(makelist(Packed))
| project Output = strcat(
    "datatable(Tier:string, SkuRetailPrice: real, SkuPrice:real, SkuEffectivePricePerGB:real, SkuEffectiveCommitmentTierThreshold:real)",
    Packed,
    "| extend ServiceName = '",ServiceNameValue,"'"
)

