let SelectedTiers = dynamic([{SentinelCT}]);
let PriceInputs = {SentinelExternalData}
| extend TierVolumeGB = iif( Tier == "Pay-as-you-go", 0, toint(Tier))
| extend JoinColumn = 1;
let UsageMetrics = 
    Usage 
    | where IsBillable == true 
    | make-series Quantity = sum(Quantity) default = 0 on StartTime from TimeRangeStart to TimeRangeEnd step 1d
    | mv-expand Quantity to typeof(long), StartTime to typeof(datetime)
    | project Quantity_GB  = (Quantity / 1e3), StartTime
    | extend JoinColumn = 1;
UsageMetrics
| join kind = inner (
    PriceInputs
    | where Tier in (SelectedTiers)
    )
    on JoinColumn
| project-away JoinColumn, JoinColumn1
| extend CostAtTier = iff(Quantity_GB > TierVolumeGB, Quantity_GB * EffectivePricePerGB, RetailPrice)
| make-series CostAtTier = sum(CostAtTier), Quantity_GB = sum(Quantity_GB) on StartTime from TimeRangeStart to TimeRangeEnd step 1d by Tier
| extend CostMovingAverage = series_fir(CostAtTier, repeat(1, 7), true, true)
| extend Quantity_GBMovingAverage = series_fir(Quantity_GB, repeat(1, 7), true, true)
