let TimeRangeStart = startofday(todatetime({TimeRange:start}),1);
let TimeRangeEnd = endofday(todatetime({TimeRange:end}),-1);
let DaysForecastedPastEnd = {DaysInFuture};
let SelectedTier = "{SentinelSelectedForecastTier}";
let PriceInputs = print Prices = dynamic({SentinelPricing})
    | mv-expand Prices
    | evaluate bag_unpack(Prices)
    | extend JoinColumn = 1;
let UsageMetrics = 
    Usage 
    | where IsBillable == true 
    | make-series Quantity = sum(Quantity) default = 0 on StartTime from TimeRangeStart to TimeRangeEnd step 1d
    | mv-expand Quantity to typeof(long), StartTime to typeof(datetime)
    | project Quantity_GB  = (Quantity / 1e3), StartTime
    | extend JoinColumn = 1;
UsageMetrics
| make-series Quantity_GB = sum(Quantity_GB) on StartTime from TimeRangeStart to datetime_add('Day', DaysForecastedPastEnd, TimeRangeEnd) step 1d 
| extend Quantity_GB_Forecasted=  series_decompose_forecast(Quantity_GB, DaysForecastedPastEnd)
| mv-expand Quantity_GB to typeof(real), Quantity_GB_Forecasted to typeof(real), StartTime to typeof(datetime)
| extend JoinColumn = 1
| join kind = inner (
    PriceInputs
    | where Tier == SelectedTier
    )
    on JoinColumn
| project-away JoinColumn, JoinColumn1
| extend CostAtTier = iff(Quantity_GB > TierVolumeGB, Quantity_GB * EffectivePricePerGB, retailPrice)
| extend CostAtTier_Forecasted = iff(Quantity_GB_Forecasted > TierVolumeGB, Quantity_GB_Forecasted * EffectivePricePerGB, retailPrice)
| make-series CostAtTier = max(CostAtTier), Forecasted = max(CostAtTier_Forecasted) on StartTime from TimeRangeStart to datetime_add('Day', DaysForecastedPastEnd, TimeRangeEnd) step 1d 
| render timechart

